def find_start_signal(signal, size_of_signal):
    for i in range(0, len(signal)-size_of_signal):
        values = set(signal[i:i+size_of_signal])
        if len(set(signal[i:i+size_of_signal])) == size_of_signal:
            return i+size_of_signal

for example, expected, size in examples:
    print(find_start_signal(example, size), expected)

